INSERT INTO `product` VALUES ('67', '199', '4545', '德国 Hipp 喜宝 2919有机纯大米米粉 350g', 'data/HIPP/4545/tu_4545_01.jpg', '<p>【产地】</p>\r\n\r\n<p>德国</p>\r\n\r\n<p>【产品配方】</p>\r\n\r\n<p>大米粉（99%），矿物质（碳酸钙、焦磷酸铁、氧化钙）。维生素（醋酸视黄酯、硝酸硫胺素，胆钙化醇）</p>\r\n\r\n<p>【温馨提示】</p>\r\n\r\n<ul>\r\n	<li>为确保宝宝健康，请遵照冲调和食用说明。</li>\r\n	<li>每餐新鲜冲调，未食用完请丢弃。</li>\r\n	<li>请用汤勺喂食。</li>\r\n	<li>为避免烫伤，请勿食用微波炉加热。</li>\r\n</ul>\r\n\r\n<p>本产品需用牛奶或者其他蛋白质的适宜液体冲调。每天食用1-2次。为确保产品充分溶解，请勿食用过高温度的奶液冲调。具体使用量请根据宝宝的实际情况进行调整。</p>\r\n\r\n<p>&nbsp;</p>\r\n', '41.32', '39.67', '特价', '350g', '500', '0', '1463729733', '2016-06-03 11:58:50');
