INSERT INTO `product` VALUES ('107', '197', '3237', '日本 wakodo 和光堂 婴儿电解饮品 苹果味 125ml*3支', 'data/JP_goods/3237/tu-3237.jpg', '<p>【产地】</p>\r\n\r\n<p>日本<br />\r\n【产品成分】</p>\r\n\r\n<p>根据宝宝的汗液成分开发而成，渗透压调整至最佳比例(260mosm/kg.H2O)，不含脂肪，着色料，保存料等刺激成分<br />\r\n【产品介绍】</p>\r\n\r\n<p>使水分更容易吸收能够及时为宝宝解渴而不增加宝<br />\r\n宝肠胃的负担，特别适合用于宝宝出汗、腹泻、发烧、夏季高温脱水等大量失水的情况，乳幼儿离子饮料是根据发烧和腹泻失掉了液体成分，为补给液体的目的制造的，部分成分与大人离子饮料不同，身体虚衰弱的时候，能放心地饮用的应该是更接近婴幼儿体液的婴幼儿<br />\r\n离子水饮料，本品可在补水的同时补充维生素C，是小宝宝补水的日常必备饮品<br />\r\n【使用方法】</p>\r\n\r\n<p>1、喝前摇一摇，附有吸管，开瓶即饮<br />\r\n&nbsp;<br />\r\n2、也可撕开锡箔纸，将液体倒入杯子或奶瓶内饮用<br />\r\n【注意事项】</p>\r\n\r\n<p>1、请勿将本品直接放在微波炉中加热<br />\r\n&nbsp;<br />\r\n2、需在大人看护下食用<br />\r\n&nbsp;<br />\r\n3、开盖前请常温避光保存</p>\r\n', '14.88', '14.88', '返利', '125ml*3支', '187', '0', '1444983754', '2016-10-24 10:26:13');
