INSERT INTO `product` VALUES ('141', '198', '3751', '澳洲 Maxigenes 美可卓 儿童补钙牛奶片 蓝莓味 150粒', 'data/Au-Goods/3751/tu-3751.jpg', '<p><br />\r\n<span style=\"font-size:16px\">【商品名称】</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">&nbsp; 澳洲 Maxigenes 美可卓 儿童补钙牛奶片 蓝莓味 150粒</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\">【产地】&nbsp;</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">&nbsp; 澳洲</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\">【产品成分】</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">&nbsp;<br />\r\n&nbsp;钙、牛奶、葡萄糖、蓝莓果萃取物粉，</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">【产品介绍】</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">澳洲Maxigenes纯天然蓝莓护眼牛奶咀嚼片由纯天然蓝莓萃取物与100%纯正高品质的澳洲牛奶组成。让您食用简单，并享受到新鲜牛奶香味与蓝莓果。100%无添加任何人造色素或防腐剂。澳洲Maxigenes&reg;纯天然蓝莓护眼牛奶咀嚼片有助于维护眼睛健康，补充身体成长所需要钙质等营养，为孩子、孕妇及成人专门制定一种每一天可以食用理想的健康食品，您一定会喜欢沉浸在Maxigenes&reg;为您提供的香浓奶香和蓝莓果香中.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">【使用方法】</span></p>\r\n\r\n<p><span style=\"font-size:16px\">蓝莓虽小，但是营养价值丰富。例如，含有极为丰富的黄酮类和多糖类化合物等，因此有&ldquo;黄金浆果&rdquo;之称。高营养含量的蓝莓，自然也伴随着很高的膳食价值和功效作用。下面就给大家说一说蓝莓的功效与作用。</span></p>\r\n\r\n<p><span style=\"font-size:16px\">1、蓝莓的果实中含有丰富的花色苷（花青素），而且其所含的花青素、有机锗、有机硒、熊果甙、氨基酸、果酸等特殊营养成分是任何植物都无法比拟的，尤其是由 16 种生物类黄酮组成的花青素，有比一般植物花青素更优越的生理活性。</span></p>\r\n\r\n<p><span style=\"font-size:16px\">2、蓝莓能有效降低胆*，并可帮助视力，减轻眼球疲劳。</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">3、蓝莓中有机酸含量约占总酸含量的一半以上。有机酸中大部分是枸橼酸，其他的有熊果酸、奎宁酸和苹果酸等。需要着重一提的是熊果酸，熊果酸又称乌索酸，属于一种弱酸性五环三萜类化合物，是多种天然产物的功能成分，具有广泛的生物学活性。</span></p>\r\n', '61.98', '59.50', '特价', '150粒', '1225', '0', '1453194891', '2016-10-15 09:39:05');
