INSERT INTO `product` VALUES ('199', '204', '3706', '德国 Sebamed 施巴 婴儿润肤乳 200ml', 'data/Germany_Goods/3706/tu_3706_01.jpg', '<p><span style=\"font-size:16px\">【商品名称】</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp; 德国 Sebamed 施巴 婴儿润肤乳 200ml</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">【产地】</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">&nbsp; 德国</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\">【产品成分】</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">&nbsp; 卵磷脂、牛油果油，杏仁油，尿囊素，泛醇等。</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\">【产品介绍】</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">&nbsp; 施巴婴幼儿护肤乳液，弱酸性配方，促进宝宝皮肤需要的酸性保护膜形成，温和不刺激，适合皮肤敏感的宝宝使用。富含天然杏仁油，牛油果油，卵磷脂等，更容易被皮肤吸收，有效补充肌肤水分，令宝宝皮肤更娇嫩细滑。</span></p>\r\n\r\n<p><span style=\"font-size:16px\">含复合保湿因数:角鲨烯,玻尿酸及维他命E,保护敏感皮肤,预防干裂</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Allantoin和维他命原B5,缓和受唾液刺激或者抓伤的皮肤</span></p>\r\n\r\n<p><span style=\"font-size:16px\">水脂均衡配方,促进幼儿皮肤的PH5.5微酸保护膜形成</span></p>\r\n\r\n<p><span style=\"font-size:16px\">快速吸收,不留油腻</span></p>\r\n\r\n<p><span style=\"font-size:16px\">通过敏感皮肤测试 &nbsp; &nbsp; &nbsp; &nbsp;</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\">【使用方法】</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">&nbsp; 沐浴后或洁净后全身护理</span></p>\r\n', '56.20', '53.72', '特价', '200ml', '187', '0', '1452142749', '2016-01-08 13:52:22');
