INSERT INTO `product` VALUES ('220', '205', '3990', '日本 Pigeon 贝亲 婴儿如厕用湿纸巾 可厕所直冲型 70片', 'data/Pigeon/3990/tu_3990_01.jpg', '<p><span style=\"font-size:16px\">【产地】</span></p>\r\n\r\n<p><span style=\"font-size:16px\">日本<br />\r\n【产品成分】</span></p>\r\n\r\n<p><span style=\"font-size:16px\">本品不含香料,酒精<br />\r\n【产品介绍】</span></p>\r\n\r\n<p><span style=\"font-size:16px\">柔滑如丝；特别坚韧；不含酒精；不含香料，呵护宝宝幼嫩的肌肤。</span></p>\r\n\r\n<p><span style=\"font-size:16px\">大尺寸且手感厚实，含有婴儿油成分，保持宝宝肌肤的润滑。可用来擦PP和身体部位，彻底清洁宝宝肌肤。</span></p>\r\n\r\n<p><span style=\"font-size:16px\">简便、清洁、换尿布时的必备品。如长期使用清水擦洗婴儿的屁股，看上去表面很干净，但却看不见其中的伤害，小屁屁的重要保护层--皮脂，已经在擦洗中渐渐变薄或消失。因此使用含天然植物精华的护肤柔湿巾，不但能有效清洁，还可以保护婴儿的皮肤健康。</span></p>\r\n\r\n<p><span style=\"font-size:16px\">【使用方法】</span></p>\r\n\r\n<p><span style=\"font-size:16px\">抽取式湿纸巾，用后请及时封闭包装，以避免湿巾变干燥。<br />\r\n【注意事项】</span></p>\r\n\r\n<p><span style=\"font-size:16px\">湿紙巾属于弱酸性，沒有任何酒精成份，可放心使用。</span></p>\r\n', '16.53', '14.88', '特价', '12ml*6', '91', '0', '1457927216', '2016-05-13 18:51:11');
