INSERT INTO `product` VALUES ('264', '325', '3419', '日本 wakodo 和光堂 婴儿润唇膏 5g', 'data/Wakodo/3419/tu-3419.jpg', '<p>【产地】</p>\r\n\r\n<p>日本<br />\r\n【产品成分】</p>\r\n\r\n<p><br />\r\n以母乳中含有的对肌肤具有保湿效果的成分为样本，磷脂（保湿成分）、保湿锁水成分及神经酰胺（保湿成分），配合橄榄油、葵花籽油、霍霍巴种子油滋润成分；高度保湿滋润。<br />\r\n【产品介绍】</p>\r\n\r\n<p>★ None for baby--无添加（防腐剂）、无合成界面活性剂、无香料、无酒精、无着色、无矿物油，初生宝宝0个月起适用；<br />\r\n★ 采用天然橄榄油、澳洲坚果油，加入维E等保湿滋润成分配合，预防寒冷干燥天气下，婴儿鼻下及唇部周围皮肤干燥；<br />\r\n★ 低刺激，敏感皮肤宝宝亦可使用。</p>\r\n\r\n<p>★ 优选2种天然安全的母乳中也特别含有的保湿成分：磷脂+神经酰胺。<br />\r\n&nbsp;<br />\r\n▲神经酰胺---增强唇部皮肤细胞的内聚力，修复皮肤屏障功能，从而缓解角质层的脱屑症状（干燥脱皮），帮助表皮再恢复。另外，神经酰胺能促进表皮的水合作用，从而改善皮肤保持水分的能力。&nbsp;<br />\r\n▲磷脂具有一定的亲水性，能为皮肤提供充分的水分和氧气，使唇部光滑柔润。&nbsp;<br />\r\n【使用方法】</p>\r\n\r\n<p>将膏体推出2-3毫米，均匀涂于宝宝的鼻下及双唇，可防止由流鼻涕、口水等造成的皮肤干裂、红肿。<br />\r\n【注意事项】</p>\r\n\r\n<p>本品已通过皮肤过敏性测试，但不表示对所有人的皮肤都不过敏。</p>\r\n', '28.93', '26.45', '特价', '5g', '483', '0', '1447309282', '2017-12-11 17:53:09');
